$(function () {	
	replaceHash();
	signBtn();
})

function replaceHash() {
	document.querySelectorAll("a").forEach((a) => {
		let href = a.getAttribute("href");
		a.href = href ?
			href.startsWith("#") && href.endsWith("#") ?
			href.replace("#", "javascript:void(0)") :
			href :
			"javascript:void(0)";
	});
}

function signInOlive() {    
    const signInOlive = document.querySelector('.signIn-olive');    
    signInOlive.classList.toggle('active');
}
function signBtn() {
	const signInBtn = document.querySelectorAll('.signUp-btn');
	signInBtn.forEach(link => {
		link.addEventListener('click', () => {
			signInOlive();			
		});
	});
}